Shaders.Shader:
#version 410
in vec4 Position;
void main() {gl_Position = Position;}

#version 410
uniform vec4 Color;
out vec4 FragColor;
void main() {FragColor = Color;}

#version 410
in vec4 Position;
in vec2 UV_0;
out vec2 UV;
void main() {
  gl_Position = Position;
  UV = UV_0;
}

#version 410
in vec2 UV;
uniform sampler2D tex2D;
out vec4 FragColor;
void main() {FragColor = texture(tex2D, UV);}
