Shaders.Shader:
#version 300 es
in vec4 Position;
void main() {gl_Position = Position;}

#version 300 es
uniform mediump vec4 Color;
out mediump vec4 FragColor;
void main() {FragColor = Color;}

#version 300 es
in vec4 Position;
in vec2 UV_0;
out vec2 UV;
void main() {
  gl_Position = Position;
  UV = UV_0;
}

#version 300 es
in mediump vec2 UV;
uniform sampler2D tex2D;
out mediump vec4 FragColor;
void main() {FragColor = texture(tex2D, UV);}

#version 300 es
struct Camera {
  mat4 projection;
  mat4 view;
  vec4 position;
};
struct Model {
  mat4 transform;
  vec4 rotation;
};
vec4 transform(Camera c, Model m, vec4 position) {
  return c.projection * c.view * m.transform * position;
}
vec3 rotate(vec4 q, vec3 v) {
  return v + 2.0 * cross(q.xyz, cross(q.xyz, v) + q.w * v);
}
vec3 rotate(Model m, vec4 normal) {
  return rotate(m.rotation, normal.xyz);
}

#include "VertexHeader"
in vec4 Position;
in vec2 UV_0;
uniform Camera camera;
uniform Model  model;
out vec2 UV;
void main() {
  gl_Position = transform(camera, model, Position);
  UV = UV_0;
}

#version 300 es
in mediump vec2 UV;
uniform sampler2D tex2D;
out mediump vec4 FragColor;
void main() {FragColor = texture(tex2D, UV);}
