ShaderFunctions.ShaderFunction:
#version 410
in vec4 Position;
uniform mat4 Model;
uniform mat4 Projection;
void main() {gl_Position = Projection * Model * Position;}

#version 410
uniform vec4 Color;
out vec4 FragColor;
void main() {FragColor = Color;}

#version 410
in vec4 Position;
in vec2 UV_0;
uniform mat4 Model;
uniform mat4 Projection;
out vec2 UV;
void main() {
  gl_Position = Projection * Model * Position;
  UV = UV_0;
}

#version 410
in vec2 UV;
uniform sampler2D tex2D;
out vec4 FragColor;
void main() {FragColor = texture(tex2D, UV);}
