ShaderFunctions.ShaderFunction:
#version 100
attribute vec4 Position;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
void main() {gl_Position = Projection * View * Model * Position;}

#version 100
uniform mediump vec4 Color;
void main() {gl_FragColor = Color;}

#version 100
attribute vec4 Position;
attribute vec2 UV_0;
uniform   mat4 ViewRot;
varying vec2 UV;
void main() {
  gl_Position = ViewRot * Position;
  UV = UV_0;
}

#version 100
attribute vec4 Position;
attribute vec2 UV_0;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
varying vec2 UV;
void main() {
  gl_Position = Projection * View * Model * Position;
  UV = UV_0;
}

#version 100
attribute vec4 Position;
attribute vec2 UV_0;
attribute vec4 Locs;
attribute vec4 UVs;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
varying vec2 UV;
vec4 adjustPos(vec4 pos, vec4 loc) {
  pos.x = (pos.x * loc.z) + loc.x;
  pos.y = (pos.y * loc.w) + loc.y;
  return pos;
}
vec2 adjustUV(vec2 uv, vec4 adj) {
  uv.x = (1.0-uv.x) * adj.x + uv.x * adj.z;
  uv.y = uv.y * adj.y + (1.0-uv.y) * adj.w;
  return uv;
}
void main() {
  gl_Position = Projection * View * Model * adjustPos(Position, Locs);
  UV = adjustUV(UV_0, UVs);
}

#version 100
varying mediump vec2 UV;
uniform sampler2D tex2D;
void main() {gl_FragColor = texture2D(tex2D, UV);}

#version 100
attribute vec4 Position;
attribute vec4 Normal;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
uniform mat4 Rotation;
varying vec3 vNormal;
void main() {
  gl_Position = Projection * View * Model * Position;
  vNormal = (Rotation * Normal).xyz;
}

#version 100
varying mediump vec3 vNormal;
uniform mediump vec4 Color;
void main() {
  gl_FragColor = vec4(Color.xyz * dot(vNormal, vec3(0.0, 1.0, 0.0)), Color.w);
}
