ShaderFunctions.ShaderFunction:
#version 300 es
in vec4 Position;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
void main() {gl_Position = Projection * View * Model * Position;}

#version 300 es
uniform mediump vec4 Color;
out mediump vec4 FragColor;
void main() {FragColor = Color;}

#version 300 es
in vec4 Position;
in vec2 UV_0;
uniform mat4 ViewRot;
out vec2 UV;
void main() {
  gl_Position = ViewRot * Position;
  UV = UV_0;
}

#version 300 es
in vec4 Position;
in vec2 UV_0;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
out vec2 UV;
void main() {
  gl_Position = Projection * View * Model * Position;
  UV = UV_0;
}

#version 300 es
in vec4 Position;
in vec2 UV_0;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
uniform vec4 Locs[100];
uniform vec4 UVs[100];
out vec2 UV;
vec4 adjustPos(vec4 pos, vec4 loc) {
  pos.x = (pos.x * loc.z) + loc.x;
  pos.y = (pos.y * loc.w) + loc.y;
  return pos;
}
vec2 adjustUV(vec2 uv, vec4 adj) {
  uv.x = (1.0-uv.x) * adj.x + uv.x * adj.z;
  uv.y = uv.y * adj.y + (1.0-uv.y) * adj.w;
  return uv;
}
void main() {
  gl_Position = Projection * View * Model * adjustPos(Position, Locs[gl_InstanceID]);
  UV = adjustUV(UV_0, UVs[gl_InstanceID]);
}

#version 300 es
in mediump vec2 UV;
uniform sampler2D tex2D;
out mediump vec4 FragColor;
void main() {FragColor = texture(tex2D, UV);}

#version 300 es
in vec4 Position;
in vec4 Normal;
uniform mat4 Model;
uniform mat4 View;
uniform mat4 Projection;
uniform mat4 Rotation;
out vec3 vNormal;
void main() {
  gl_Position = Projection * View * Model * Position;
  vNormal = (Rotation * Normal).xyz;
}

#version 300 es
in mediump vec3 vNormal;
uniform mediump vec4 Color;
out mediump vec4 FragColor;
void main() {
  FragColor = vec4(Color.xyz * dot(vNormal, vec3(0.0, 1.0, 0.0)), Color.w);
}
